import { useParams } from "react-router-dom";
import { items } from "../data"; // Assuming items data is here

export const ItemDetails = () => {
  const { itemId } = useParams(); // Get the itemId from URL
  const item = items.find((i) => i.id === parseInt(itemId)); // Find the item by id

  if (!item) {
    return <h3>Item not found</h3>;
  }

  return (
    <div className="item-details">
      <h3>{item.name}</h3>
      <img src={item.image} alt={item.name} />
      <p>{item.description}</p> {/* Assuming description exists in data */}
    </div>
  );
};
