import {
          DECREMENT_COUNTER,
          INCREMENT_COUNTER,
          RESET_COUNTER
        } from "../actions/counterActions";
        
        const INITIAL_STATE = { count: 0 };
        
        export const counterReducer = (state = INITIAL_STATE, action) => {
          switch (action.type) {
            case INCREMENT_COUNTER:
              return { count: ++state.count };
            case DECREMENT_COUNTER:
              return { count: --state.count };
        
            case RESET_COUNTER:
              return { count: 0 };
            default:
              return state;
          }
        };
