// create and export middleware function here
// Store , next, action
 export const LoggerMiddleware =(store)=>{
          return (next) => (action) => {
            if(action.type === 'LOGIN'){
              const user = store.getState().user;
              if(!user){
                // dispatch an action to fetch user data
                store.dispatch({type: 'FETCH_USER_DATA'});
              }
            }
            return next(action);
          }
}
