import { configureStore } from "@reduxjs/toolkit";
const { timerReducer } = require("./redux/reducers/timerReducer");
const { counterReducer } = require("./redux/reducers/counterReducer");
const { LoggarMiddleware } = require("./redux/middlewares/loggerMiddleware");

// Add the middleware function here
export const store = configureStore({
  reducer: {
    timer: timerReducer,
    counter: counterReducer
  },
  middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(LoggarMiddleware)
});
